import { useEffect, useState, useRef } from "react";
import "./ContentDetails.css";
import "./Loader.css";
import Content from "./Content";
import ContentDetails from "./ContentDetails";
import { ControlBar } from "./ControlBar";
import Card from "./Card";

export default function Anime() {
  const [chosen, setChosen] = useState({});
  const [query, setQuery] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  // const [initialData, SetInitialData] = useState([]);
  const [isBack, setIsBack] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const initialData = useRef([]);

  useEffect(
    function () {
      const controller = new AbortController();
      async function getData() {
        try {
          setIsLoading(true);

          const res = await fetch(
            `https://api.jikan.moe/v4/anime?q=${query}&sfw=true`,
            {
              signal: controller.signal,
            }
          );
          const data = await res.json();
          setSearchResult(data.data);
        } catch (err) {
          if (err.name !== "AbortError") {
            console.log(err.message);
          }
        } finally {
          setIsLoading(false);
        }
      }

      if (query.length < 3) {
        setSearchResult([]);
        return;
      }

      getData();
      return function () {
        console.log("Abort Request");
        controller.abort();
      };
    },
    [query]
  );

  useEffect(function () {
    async function getInitialData() {
      try {
        setIsLoading(true);
        const res = await fetch(`https://api.jikan.moe/v4/anime?q&sfw=true`);
        const data = await res.json();
        initialData.current = data.data;
      } catch (err) {
        console.log(err.message);
      } finally {
        setIsLoading(false);
      }
    }
    getInitialData();
  }, []);

  function handleIsBack() {
    console.log("handleIsBack");
    setIsBack((isBack) => !isBack);
  }

  function handleSearch(queryValue) {
    console.log("handleSearch");
    setIsBack(true);
    setQuery(queryValue);
  }

  function handleChoice(chosenAnime) {
    console.log("handleChoice");
    setChosen(chosenAnime);
    handleIsBack();
  }

  function handleSort(sortType) {
    console.log("handleSort");
    switch (sortType) {
      case "name":
        setSearchResult((result) =>
          result.slice().sort((a, b) => a.title.localeCompare(b.title))
        );
        break;
      case "epi":
        setSearchResult((result) =>
          result.slice().sort((a, b) => a?.episodes - b?.episodes)
        );
        break;
      default:
        console.log("something Went Wrong");
    }
  }

  return (
    <div className="anime">
      <ControlBar search={query} onSearch={handleSearch} onSort={handleSort} />
      {isBack ? (
        <Content searchResult={searchResult}>
          {isLoading ? (
            <div className="loader-box">
              <div class="loader"></div>
            </div>
          ) : (
            searchResult?.map((el) => (
              <Card animeData={el} key={el?.title} onChoice={handleChoice} />
            ))
          )}
        </Content>
      ) : (
        <ContentDetails chosenAnime={chosen} onBack={handleIsBack} />
      )}
    </div>
  );
}

