import { useEffect, useState } from "react";
import "./ContentDetails.css";
import Content from "./Content";
import ContentDetails from "./ContentDetails";

export default function Anime() {
  // we have only one page no need for this
  useEffect(() => {
    document.getElementById(
      "body"
    ).style.backgroundImage = `url("../images/animeback.jpg")`;
  }, []);

  const [chosen, setChosen] = useState([]);

  useEffect(() => {
    async function getData() {
      const res = await fetch(
        "https://api.jikan.moe/v4/anime?q=jujutsu kaisen&sfw"
      );
      const data = await res.json();

      setChosen(data.data);
    }
    getData();
  }, []);

  return (
    <div>
      <ControlBar />
      {/* <div className="content-details-page">
        <ContentDetails />
      </div> */}
      <div className="anime"> {<Content />}</div>;
    </div>
  );
}

// ******************************************************************************************
function ControlBar() {
  return (
    <div className="control-bar">
      <form className="search-form">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 256 256"
          className="search-form-svg"
        >
          <path d="M229.66,218.34l-50.07-50.06a88.11,88.11,0,1,0-11.31,11.31l50.06,50.07a8,8,0,0,0,11.32-11.32ZM40,112a72,72,0,1,1,72,72A72.08,72.08,0,0,1,40,112Z"></path>
        </svg>
        <input placeholder="Search" type="text" id="search-input-field" />
      </form>

      <div className="sort-box">
        <label htmlFor="sort-box-label">Sort by</label>
        <select id="sort-box-select">
          <option>Name</option>
          <option>Latest</option>
          <option>FavoriteðŸ˜Š</option>
        </select>
      </div>
    </div>
  );
}
