import { useEffect, useReducer, useState } from "react";
import "./ContentDetails.css";
import Content from "./Content";
import ContentDetails from "./ContentDetails";
import { ControlBar } from "./ControlBar";
import Card from "./Card";

function reducer(state, action) {
  switch (action.type) {
    case "name":
      return state.silce().sort((a, b) => a.title.localCompare(b.title));
    case "ep":
      return state.silce().sort((a, b) => a?.episodes - b?.episodes);

    default:
      console.log("something Went Wrong");
  }
}

export default function Anime() {
  const initialSate = {
    sort: "",
    search: "",
    chosen: {},
    defaultData: [],
    searchResult: [],
    back: false,
  };

  const [chosen, setChosen] = useState({});
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  // const [state, dispatch] = useReducer(reducer, []);

  useEffect(
    function () {
      const controller = new AbortController();
      async function getData() {
        console.log(search);
        const res = await fetch(
          `https://api.jikan.moe/v4/anime?q=${search} &sfw=true`,
          { signal: controller.signal }
        );
        const data = await res.json();
        setSearchResult(data.data);
      }
      getData();

      return function () {
        controller.abort();
      };
    },
    [search]
  );
  // useEffect(
  //   function () {
  //     const controller = new AbortController();
  //     async function getData() {
  //       console.log(search);
  //       const res = await fetch(
  //         `https://api.jikan.moe/v4/anime?q=${search} &sfw=true`,
  //         { signal: controller.signal }
  //       );
  //       const data = await res.json();
  //       setSearchResult(data.data);
  //     }
  //     getData();

  //     return function () {
  //       controller.abort();
  //     };
  //   },
  //   [search]
  // );

  function handleSearch(name) {
    console.log(name);
    setSearch(name);
  }

  function handleChoice(chosenAnime) {
    console.log(chosenAnime);
    setChosen(chosenAnime);
  }

  console.log(search);

  return (
    <div className="anime">
      <ControlBar search={search} onSearch={handleSearch} />
      <ContentDetails chosenAnime={chosen} />
      <Content searchResult={searchResult}>
        {searchResult?.map((el) => (
          <Card animeData={el} key={el?.title} onChoice={handleChoice} />
        ))}
      </Content>
    </div>
  );
}
