import { useEffect, useState } from "react";
import "./ContentDetails.css";
import "./Loader.css";
import Content from "./Content";
import ContentDetails from "./ContentDetails";
import { ControlBar } from "./ControlBar";
import Card from "./Card";

export default function Anime() {
  const [chosen, setChosen] = useState({});
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [isBack, setIsBack] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(
    function () {
      const controller = new AbortController();
      async function getData() {
        const res = await fetch(
          `https://api.jikan.moe/v4/anime?q=${search}&sfw=true`,
          {
            signal: controller.signal,
          }
        );
        const data = await res.json();
        setSearchResult(data.data);
      }
      getData();

      return function () {
        console.log("Abort Request");
        controller.abort();
      };
    },
    [search]
  );

  function handleIsBack() {
    console.log("handleIsBack");
    setIsBack((isBack) => !isBack);
  }

  function handleSearch(name) {
    console.log("handleSearch");
    setIsBack(true);
    setSearch(name);
  }

  function handleChoice(chosenAnime) {
    console.log("handleChoice");
    setChosen(chosenAnime);
    handleIsBack();
  }

  function handleSort(sortType) {
    console.log("handleSort");
    switch (sortType) {
      case "name":
        setSearchResult((result) =>
          result.slice().sort((a, b) => a.title.localeCompare(b.title))
        );
        break;
      case "epi":
        setSearchResult((result) =>
          result.slice().sort((a, b) => a?.episodes - b?.episodes)
        );
        break;
      default:
        console.log("something Went Wrong");
    }
  }

  return (
    <div className="anime">
      <ControlBar search={search} onSearch={handleSearch} onSort={handleSort} />
      {isBack ? (
        <Content searchResult={searchResult}>
          isLoading ? (
          <div className="loader-box">
            <div class="loader"></div>
          </div>
          ) : ( searchResult?.map((el) => (
          <Card animeData={el} key={el?.title} onChoice={handleChoice} />
          )) );
        </Content>
      ) : (
        <ContentDetails chosenAnime={chosen} onBack={handleIsBack} />
      )}
    </div>
  );
}

