import { useEffect, useState } from "react";

export default function ContentDetails() {
  // const [search, setSearch] = useState([]);

  const [chosen, setChosen] = useState([]);

  useEffect(() => {
    async function getData() {
      const res = await fetch(
        "https://api.jikan.moe/v4/anime?q=jujutsu kaisen&sfw"
      );
      const data = await res.json();
      setChosen(data.data);
    }
    getData();
  }, []);

  return (
    <div className="content-details-container">
      <div className="item-poster-box">
        <img
          src={"https://cdn.myanimelist.net/images/anime/1171/109222l.jpg"}
          alt="k"
        />
      </div>
      <Aside chosen={chosen} />
      <Status chosen={chosen} />
      <p className="item-description">{chosen[0]?.synopsis}</p>
    </div>
  );
}

function Aside({ chosen }) {
  const producers = chosen[0]?.producers
    ?.map((el) => el)
    .map((el) => el.name)
    .join(" , ");

  const licensors = chosen[0]?.licensors
    ?.map((el) => el)
    .map((el) => el.name)
    .join(" , ");

  return (
    <div className="aside-item-box">
      <div className="aside-item">
        <span className="aside-item">Type: {chosen[0]?.type}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Episodes: {chosen[0]?.episodes}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Status: {chosen[0]?.status}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Aired: {chosen[0]?.aired?.string}</span>
      </div>
      {/* <div className="aside-item">
        <span className="aside-item">Premiered: {chosen[0]?.pr}</span>
      </div> */}
      <div className="aside-item">
        <span className="aside-item">Producers: {producers}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Licensors:{licensors}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">
          Studios: {chosen[0]?.studios[0]?.name}
        </span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Source: {chosen[0]?.source}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Theme: {chosen[0]?.themes[0]?.name}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">
          Demographic: {chosen[0]?.demographics[0]?.name}
        </span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Duration: {chosen[0]?.duration}</span>
      </div>
      <div className="aside-item">
        <span className="aside-item">Rating: {chosen[0]?.rating}</span>
      </div>
    </div>
  );
}

function Status({ chosen }) {
  console.log(chosen[0]);
  return (
    <div className="status-box">
      <div className="status-item-score-box">
        <p className="status-item-score">{chosen[0]?.score}</p>
        <p className="status-item-score-users">Users: 2,093,317 </p>
      </div>
      <div className="status-item">
        <span className="status-item-type">Ranked</span>
        <p className="status-item-value">#{chosen[0]?.rank}</p>
      </div>
      <div className="status-item">
        <span className="status-item-type">Popularity</span>
        <p className="status-item-value">#{chosen[0]?.popularity}</p>
      </div>
      <div className="status-item">
        <span className="status-item-type">Members</span>
        <p className="status-item-value">{chosen[0]?.members}</p>
      </div>
    </div>
  );
}
