import "./Content.css";

export default function Content({ data }) {
  // console.log(data);

  return (
    <div className="content-page">
      <ControlBar />
      <div className="content-box">
        {data.map((card) => (
          <Card cardData={card} />
        ))}
      </div>
    </div>
  );
}

async function animeData() {
  const res = await fetch(
    "https://api.jikan.moe/v4/anime?q=jujutsu kaisen&sfw"
  );
  const data = await res.json();
  console.log(data);
}

animeData();

function ControlBar() {
  return (
    <div className="control-bar">
      <form className="search-form">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 256 256"
          className="search-form-svg"
        >
          <path d="M229.66,218.34l-50.07-50.06a88.11,88.11,0,1,0-11.31,11.31l50.06,50.07a8,8,0,0,0,11.32-11.32ZM40,112a72,72,0,1,1,72,72A72.08,72.08,0,0,1,40,112Z"></path>
        </svg>
        <input placeholder="Search" type="text" id="search-input-field" />
      </form>

      <div className="sort-box">
        <label for="sort-box-label">Sort by</label>
        <select id="sort-box-select">
          <option>Name</option>
          <option>Latest</option>
          <option>FavoriteðŸ˜Š</option>
        </select>
      </div>
    </div>
  );
}

function Card({ cardData }) {
  const { name, image, date, description } = cardData;
  // console.log(image);
  return (
    <div className="content-card">
      {/* <div className="content-card-face"><img src={image}></img></div> */}

      <div className="content-card-back">
        <h2>{name}</h2>
        <br />
        <p>
          <strong>Release date:</strong>
          {date}
        </p>
        <br />
        <p>
          <strong>Description:</strong>
          <br />
          {description}
        </p>
      </div>
    </div>
  );
}
