import { useEffect, useState } from "react";
import "./ContentDetails.css";
import Content from "./Content";
import ContentDetails from "./ContentDetails";
import { ControlBar } from "./ControlBar";

export default function Anime() {
  const [chosen, setChosen] = useState([]);
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  function handleSearch(name) {
    console.log(name);
    setSearch(name);
  }

  console.log(search);
  // ("https://api.jikan.moe/v4/anime?q=jujutsu kaisen&sfw");
  // https://api.jikan.moe/v4/anime?q=ju&sfw
  // `https://api.jikan.moe/v4/anime?q=${search}`
  //
  useEffect(() => {
    async function getData() {
      console.log(search);
      // const res = await fetch("https://api.jikan.moe/v4/anime?q=naruto &?sfw");
      const res = await fetch(
        "https://api.jikan.moe/v4/anime?q=jujitus kaisn&?nsfw"
      );
      const data = await res.json();
      setSearchResult(data.data);
    }
    getData();
  }, [search]);

  return (
    <div className="anime">
      <ControlBar search={search} onSearch={handleSearch} />
      {/* <div className="content-details-page">
        <ContentDetails />
      </div> */}
      <Content searchResult={searchResult} />
    </div>
  );
}
