import SeparationLine from './SeparationLine';

const BenefitData = [
  {
    title: 'BENEFIT 01',
    text: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Maxime, quia. Atque quam, maiores iure enim, sunt porro quia quae provident voluptates excepturi necessitatibus saepe temporibus delectus iste perspiciatis vel quis magni inventore odio numquam labore nisi distinctio obcaecati. Facere, quam!',
  },
  {
    title: 'BENEFIT 02',
    text: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Maxime, quia. Atque quam, maiores iure enim, sunt porro quia quae provident voluptates excepturi necessitatibus saepe temporibus delectus iste perspiciatis vel quis magni inventore odio numquam labore nisi distinctio obcaecati. Facere, quam!',
  },
  {
    title: 'BENEFIT 03',
    text: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Maxime, quia. Atque quam, maiores iure enim, sunt porro quia quae provident voluptates excepturi necessitatibus saepe temporibus delectus iste perspiciatis vel quis magni inventore odio numquam labore nisi distinctio obcaecati. Facere, quam!',
  },
];

export function WhyCrypto() {
  return (
    <div className=" bg-[#e7e7e7] text-primary lg:mt-32">
      {/* <div className="bg-black h-px col-span-3  absolute left-0 w-screen"></div> */}
      ;
      <div className="gap4 container relative mx-auto flex flex-col items-center text-center lg:grid lg:grid-cols-3 lg:gap-x-28">
        <h1 className="text-center lg:col-span-3 lg:text-xl">
          WE MADE IT EASIER
        </h1>
        <h2 className="text-center lg:col-span-3 lg:mb-40 lg:text-7xl">
          Why Crypto?
        </h2>

        {/* <SeparationLine /> */}

        <div className=" lg:col-start-1">
          <Benefit benefit={BenefitData[0]} />
        </div>
        <div className=" lg:col-start-2">
          <Benefit benefit={BenefitData[1]} />
        </div>
        <div className=" lg:col-start-3">
          <Benefit benefit={BenefitData[2]} />
        </div>
      </div>
    </div>
  );
}

function Benefit({ benefit }) {
  const { title, text } = benefit;

  return (
    <div>
      <h3 className="">{title}</h3>
      <p className="">{text}</p>
    </div>
  );
}
