const Sections = [
  {
    title: "01 - Versatile",
    text: " Lorem ipsum, dolor sit amet consectetur adipisicing elit.Assumenda molestiae pariatur aspernatur! Similique dolore at labore optio inventore eos officiis esse modi minima earum dignissimos! Architecto, possimus sapiente quisquam cumque quas sint repellat delectus beatae recusandae impedit dicta minima eos, magni molestiae voluptatem tempora ut quis asperiores ratione repudiandae explicabo.",
  },
  {
    title: "03 - Transparent",
    text: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Assumenda molestiae pariatur aspernatur! Similique dolore at labore optio inventore eos, officiis esse modi minima earum dignissimos! Architecto, impedit dicta minima eos, magni molestiae voluptatem tempora ut quis asperiores ratione repudiandae explicabo.",
  },
  {
    title: "02 - Easy To Use",
    text: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Assumenda molestiae pariatur aspernatur! Similique dolore at labore optio inventore eos, officiis esse modi minima earum dignissimos! Architecto, possimus sapiente quisquam cumque quas sint repellat delectus beatae recusandae impedit dicta minima eos, magni molestiae voluptatem tempora ut quis asperiores ratione repudiandae explicabo.",
  },
  {
    title: "04 - Real Time",
    text: "Lorem ipsum, dolor sit amet consectetur adipisicing elit. Assumenda molestiae pariatur aspernatur! Similique dolore at labore optio inventore eos, officiis esse modi minima earum dignissimos! Architecto, impedit dicta minima eos, magni molestiae voluptatem tempora ut quis asperiores ratione repudiandae explicabo.",
  },
];

export function Features() {
  return (
    <div className="grid grid-cols-4">
      {Sections.map((el) => (
        <FeatureSection sectionData={el} />
      ))}

      <FeatureSection />

      <div className="app-with-text bg-amber-600 col-span-2">
        <h1 className="">Features</h1>
        <div className="app">
          <img src="/Images/Logo.png" alt="" />
          <h2 className="logo-text">Coinmyte.</h2>
          <h3>EURO to GC</h3>

          <form action="">
            <label for="from">From</label>
            <br />
            <input type="text" id="from" />
            <br />
            <label for="to">To</label>
            <br />
            <input type="text" id="to" />
            <br />

            <h4>YOUR TOTAL</h4>

            <button>
              <a href="#">TRADE NOW</a>
            </button>
          </form>

          <output></output>

          <div className="app-footer">
            <span className="app-footer-links">
              <a href="#">PRICING</a>
            </span>
            <span className="app-footer-links">
              <a href="#">FAQS</a>
            </span>
            <span className="app-footer-links">
              <a href="#">TRADING BASICS</a>
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

function FeatureSection({ sectionData }) {
  const { title, text } = sectionData;

  return (
    <section className="bg-green-500">
      <h2 className="">{title}</h2>
      <p className="">{text}</p>
    </section>
  );
}
